import React, { useState, useEffect } from 'react';
import { Button } from 'primereact/button';
import { Panel } from 'primereact/panel';
import Code from '../../components/Code';
import { RadioButton } from 'primereact/radiobutton';

const code = `
    int num = 5;

    for (int i = 0; i < num; i++)
    {
        print("I have been called " + i + " times.");
    }
  `;


function VariablesQuiz() {
    const [answer, setAnswer] = useState(null);
    var correctAnswer = 3;


    return (
        <div>
            <h1>Variables Quiz</h1>
            <Panel header="Question 1" toggleable>
                <p>Have a look at this code segment - It contains a for loop that runs X amount of times.</p>
                <Code  code={code} language="javascript"/>
                <p>What will be the final output to the console?</p>

                <div className="card">
                <div className="p-field-radiobutton">
                    <RadioButton inputId="answer1" name="answer" value="0" onChange={(e) => setAnswer(e.value)} checked={answer === 'answer1'}/>  
                    <label htmlFor="answer1">I have been called 0 times.</label>
                </div>
                <div className="p-field-radiobutton">
                    <RadioButton inputId="answer2" name="answer" value="1" onChange={(e) => setAnswer(e.value)} checked={answer === 'answer2'}/>  
                    <label htmlFor="answer2">I have been called 5 times.</label>
                </div>
                <div className="p-field-radiobutton">
                    <RadioButton inputId="answer3" name="answer" value="2" onChange={(e) => setAnswer(e.value)} checked={answer === 'answer3'}/>  
                    <label htmlFor="answer3">I have been called 3 times.</label>
                </div>
                <div className="p-field-radiobutton">
                    <RadioButton inputId="answer4" name="answer" value="3" onChange={(e) => setAnswer(e.value)} checked={answer === 'answer4'}/>  
                    <label htmlFor="answer4">I have been called 4 times.</label>
                </div>
            </div>
            </Panel>

            {(() => {
                if (answer == correctAnswer) {
                    return (
                    <p>Correct!</p>
                    )
                }
                else if (answer == null)
                {
                    return (null)
                }
                else {
                    return (
                    <p>Wrong!</p>
                    )
                }
            })()}

        </div>
    )
}

export default VariablesQuiz
