import Unity, { UnityContext } from 'react-unity-webgl';
import React, { useState, useEffect } from "react";
import './UnityApp.css'

function UnityApp() {

    const unityContext = new UnityContext({
        loaderUrl: "unity/scene.loader.js",
        dataUrl: "unity/scene.data",
        frameworkUrl: "unity/scene.framework.js",
        codeUrl: "unity/scene.wasm",
      });

    {/* 
    React -> Unity communication 
    "Collider" = name of GameObject
    "SetLabel" = name of method attached to one of the GameObjects scripts
    */}

    const [counter, setCounter] = useState(4);

    function setText() {
        setCounter(counter+1);
        unityContext.send("Label", "SetLabel", counter);
    }

    {/* 
        Unity -> React communication 
        "eventName" = name of listener
        "eventListener" = name of method to be invoked
        function on(eventName: string, eventListener: Function): void;

        for more Unity -> React steps: 
        https://www.npmjs.com/package/react-unity-webgl#getting-started

    */}

    const [number, setNumber] = useState(0);

    useEffect(function () {
        unityContext.on("GetLabel", function (number) {
        setNumber(number);
        });
    }, []);

    return (
        <div>
            <button onClick={setText} className='btn'>Add to counter</button>
            <div className='unity-wrapper'>
                <Unity 
                unityContext={unityContext}
                style={{
                    height: "100%",
                    width: "100%",
                }}
                />
                <p>{number}</p>
            </div>
        </div>

    )
}

export default UnityApp
