import React, { useState, useEffect } from 'react';
import { Button } from 'primereact/button';
import { Panel } from 'primereact/panel';
import Code from '../../components/Code';
import { RadioButton } from 'primereact/radiobutton';

const code = `
    int num = 5;

    for (int i = 0; i < num; i++)
    {
        print("I have been called " + i + " times.");
    }
  `;

function VariablesQuiz() {
    const [answer1, setAnswer1] = useState(null);
    const [answer2, setAnswer2] = useState(null);
    const [answer3, setAnswer3] = useState(null);
    const [answer4, setAnswer4] = useState(null);
    const [answer5, setAnswer5] = useState(null);
    var correctAnswer1 = 0;
    var correctAnswer2 = 0;
    var correctAnswer3 = 0;
    var correctAnswer4 = 0;
    var correctAnswer5 = 0;

    function CheckAnswers() {
        let correctCount = 0;
        if (answer1 == correctAnswer1) { correctCount = correctCount + 1; }
        if (answer2 == correctAnswer2) { correctCount = correctCount + 1; }
        if (answer3 == correctAnswer3) { correctCount = correctCount + 1; }
        if (answer4 == correctAnswer4) { correctCount = correctCount + 1; }
        if (answer5 == correctAnswer5) { correctCount = correctCount + 1; }
        console.log(correctCount);
    }

    return (
        <div>
            <h1>Variables Quiz</h1>
            <Panel header="Question 1" toggleable>
                <p>Have a look at this code segment - It contains a for loop that runs X amount of times.</p>
                <Code  code={code} language="javascript"/>
                <p>What will be the final output to the console?</p>

                <div className="card">
                    <div className="p-field-radiobutton">
                        <RadioButton inputId="answer1" name="answer" value="0" onChange={(e) => setAnswer1(e.value)} checked={answer1 === 'answer1'}/>  
                        <label htmlFor="answer1">I have been called 0 times.</label>
                    </div>
                    <div className="p-field-radiobutton">
                        <RadioButton inputId="answer2" name="answer" value="1" onChange={(e) => setAnswer1(e.value)} checked={answer1 === 'answer2'}/>  
                        <label htmlFor="answer2">I have been called 5 times.</label>
                    </div>
                    <div className="p-field-radiobutton">
                        <RadioButton inputId="answer3" name="answer" value="2" onChange={(e) => setAnswer1(e.value)} checked={answer1 === 'answer3'}/>  
                        <label htmlFor="answer3">I have been called 3 times.</label>
                    </div>
                    <div className="p-field-radiobutton">
                        <RadioButton inputId="answer4" name="answer" value="3" onChange={(e) => setAnswer1(e.value)} checked={answer1 === 'answer4'}/>  
                        <label htmlFor="answer4">I have been called 4 times.</label>
                    </div>
                </div>
            </Panel>
            <Button label="Submit" onClick={CheckAnswers}></Button>

        </div>
    )
}

export default VariablesQuiz
