import React, { useState, useEffect } from 'react';
import { RadioButton } from 'primereact/radiobutton';
import { Button } from 'primereact/button';

function Answers(props) {
    const [answer, setAnswer] = useState(null);
    const correctAnswer = 3;

    function chk() {
        if (answer == correctAnswer) {
            console.log('correct');
        }
        else
        {
            console.log('wrong');
        }
    }

    return (

        <div className="card">
            <div className="p-field-radiobutton">
                <RadioButton inputId="answer1" name="answer" value="0" onChange={(e) => setAnswer(e.value)} checked={answer === 0}/>  
                <label htmlFor="answer1">{props.answer1}</label>
            </div>
            <div className="p-field-radiobutton">
                <RadioButton inputId="answer2" name="answer" value="1" onChange={(e) => setAnswer(e.value)} checked={answer === 1}/>  
                <label htmlFor="answer2">{props.answer2}</label>
            </div>
            <div className="p-field-radiobutton">
                <RadioButton inputId="answer3" name="answer" value="2" onChange={(e) => setAnswer(e.value)} checked={answer === 2}/>  
                <label htmlFor="answer3">{props.answer3}</label>
            </div>
            <div className="p-field-radiobutton">
                <RadioButton inputId="answer4" name="answer" value="3" onChange={(e) => setAnswer(e.value)} checked={answer === 3}/>  
                <label htmlFor="answer4">{props.answer4}</label>
            </div>
            <h3>{answer}</h3>
            <Button label="Submit" onClick={chk}></Button>
        </div>
    )
}

export default Answers
