import React, { useState, useEffect } from 'react';
import { RadioButton } from 'primereact/radiobutton';
import { Button } from 'primereact/button';

function Answers(props) {
    const [answer, setAnswer] = useState("0");

    function Changed() {
        console.log("changed called");
        console.log("answer = " + answer);
        if (answer == props.correctAnswer)
        {
            console.log("set to true");
            props.setCorrect(true);
        }
    }

    return (

        <div className="card">
            <div className="p-field-radiobutton">
                <RadioButton inputId="a" name="answers" value="0" onChange={(e) => { setAnswer(e.value); Changed(); } } checked={answer === "0"}/>  
                <label htmlFor="answer1">{props.answer1}</label>
            </div>
            <div className="p-field-radiobutton">
                <RadioButton inputId="b" name="answers" value="1" onChange={(e) => { setAnswer(e.value); Changed(); } } checked={answer === "1"}/>  
                <label htmlFor="answer2">{props.answer2}</label>
            </div>
            <div className="p-field-radiobutton">
                <RadioButton inputId="c" name="answers" value="2" onChange={(e) => { setAnswer(e.value); Changed(); } } checked={answer === "2"}/>  
                <label htmlFor="answer3">{props.answer3}</label>
            </div>
        <p>Chosen RadioButton {answer}</p>
        </div>


    )
}

export default Answers

