import React, { useState, useEffect } from 'react';
import { RadioButton } from 'primereact/radiobutton';
import { Button } from 'primereact/button';

function Answers(props) {
    const [answer, setAnswer] = useState(null);

    function checkForCorrect() {
        if (answer == props.correctAnswer)
        {
            props.setCorrect(true);
            console.log("set to true");
        }
    }

    function changed(value) {
        console.log("passed in " + value);
        setAnswer(value);
        console.log("testing " + value + " and " + propscorrectAnswer);
        if (answer == props.correctAnswer)
        {
            props.setCorrect(true);
            console.log("set to true");
        }
    }

    return (

        <div className="card">
            <div className="p-field-radiobutton">
                <RadioButton inputId="a" name="answers" value="0" onChange={(e) => changed(e.value)} checked={answer === "0"}/>  
                <label htmlFor="answer1">{props.answer1}</label>
            </div>
            <div className="p-field-radiobutton">
                <RadioButton inputId="b" name="answers" value="1" onChange={(e) => setAnswer(e.value)} checked={answer === "1"}/>  
                <label htmlFor="answer2">{props.answer2}</label>
            </div>
            <div className="p-field-radiobutton">
                <RadioButton inputId="c" name="answers" value="2" onChange={(e) => setAnswer(e.value)} checked={answer === "2"}/>  
                <label htmlFor="answer3">{props.answer3}</label>
            </div>
            <div className="p-field-radiobutton">
                <RadioButton inputId="d" name="answers" value="3" onChange={(e) => setAnswer(e.value)} checked={answer === "3"}/>  
                <label htmlFor="answer4">{props.answer4}</label>
            </div>
        </div>
    )
}

export default Answers
