import React, { useState, useEffect } from 'react';
import { Button } from 'primereact/button';
import { Panel } from 'primereact/panel';
import Code from '../../components/Code';
import { RadioButton } from 'primereact/radiobutton';
import Answers from './Answers';

const code = `
    int num = 5;

    for (int i = 0; i < num; i++)
    {
        print("I have been called " + i + " times.");
    }
  `;

function VariablesQuiz() {

    const [question1, setQuestion1] = useState(false);
    const [question2, setQuestion2] = useState(false);
    const [question3, setQuestion3] = useState(false);

    const [answer1, setAnswer1] = useState(null);

    return (
        <div>
            <h1>Variables Quiz</h1>
            {question1 ? <h2>Question 1 - Correct!</h2> : <h2>Question 1</h2>}
            {question2 ? <h2>Question 2 - Correct!</h2> : <h2>Question 2</h2>}
            {question3 ? <h2>Question 3 - Correct!</h2> : <h2>Question 3</h2>} 

                <Panel header="Question 1" toggleable className="p-m-5">
                    {/* Question Text */}
                    <p>This is question 1, the answer is option 1</p>
                    {/* Answers */}
                    <div className="card">
                        <div className="p-field-radiobutton">
                            <RadioButton inputId="a" name="answers" value="1" onChange={(e) => setAnswer1(e.value)} checked={answer1 === '1'}/>  
                            <label htmlFor="answer1">a</label>
                        </div>
                        <div className="p-field-radiobutton">
                            <RadioButton inputId="b" name="answers" value="2" onChange={(e) => setAnswer1(e.value)} checked={answer1 === '2'}/>  
                            <label htmlFor="answer2">b</label>
                        </div>
                        <div className="p-field-radiobutton">
                            <RadioButton inputId="c" name="answers" value="3" onChange={(e) => setAnswer1(e.value)} checked={answer1 === '3'}/>  
                            <label htmlFor="answer3">c</label>
                        </div>
                    </div>
                </Panel>

                <Panel header="Question 2" toggleable className="p-m-5">
                    {/* Question Text */}
                    <p>This is question 2, the answer is option 2</p>
                    {/* Answers */}
                    <div className="card">
                        <div className="p-field-radiobutton">
                            <RadioButton inputId="a" name="answers" value="1" onChange={(e) => setAnswer(e.value)} checked={answer === '1'}/>  
                            <label htmlFor="answer1">a</label>
                        </div>
                        <div className="p-field-radiobutton">
                            <RadioButton inputId="b" name="answers" value="2" onChange={(e) => setAnswer(e.value)} checked={answer === '2'}/>  
                            <label htmlFor="answer2">b</label>
                        </div>
                        <div className="p-field-radiobutton">
                            <RadioButton inputId="c" name="answers" value="3" onChange={(e) => setAnswer(e.value)} checked={answer === '3'}/>  
                            <label htmlFor="answer3">c</label>
                        </div>
                    </div>
                </Panel>

                <Panel header="Question 3" toggleable className="p-m-5">
                    {/* Question Text */}
                    <p>This is question 3, the answer is option 3</p>
                    {/* Answers */}
                    <div className="card">
                        <div className="p-field-radiobutton">
                            <RadioButton inputId="a" name="answers" value="1" onChange={(e) => setAnswer(e.value)} checked={answer === '1'}/>  
                            <label htmlFor="answer1">a</label>
                        </div>
                        <div className="p-field-radiobutton">
                            <RadioButton inputId="b" name="answers" value="2" onChange={(e) => setAnswer(e.value)} checked={answer === '2'}/>  
                            <label htmlFor="answer2">b</label>
                        </div>
                        <div className="p-field-radiobutton">
                            <RadioButton inputId="c" name="answers" value="3" onChange={(e) => setAnswer(e.value)} checked={answer === '3'}/>  
                            <label htmlFor="answer3">c</label>
                        </div>
                    </div>
                </Panel>

            <Button label="Submit" onClick={''}></Button>

        </div>
    )
}

export default VariablesQuiz
