import React, { useState, useEffect } from 'react';
import { RadioButton } from 'primereact/radiobutton';
import { Button } from 'primereact/button';

function Answers(props) {
    const [answer, setAnswer] = useState(null);

    function chk() {
        if (answer == props.correctAnswer) {
            console.log('correct');
        }
        else
        {
            console.log('wrong');
        }
    }

    return (

        <div className="card">
            <div className="p-field-radiobutton">
                <RadioButton inputId={props.id} name={props.name} value="{props.id} + 0" onChange={(e) => setAnswer(e.value)} checked={answer === "{props.id} + 0"}/>  
                <label htmlFor="answer1">{props.answer1}</label>
                <p>"{props.id}+0"</p>
            </div>
            <div className="p-field-radiobutton">
                <RadioButton inputId={props.id} name={props.name} value="{props.id} + 1" onChange={(e) => setAnswer(e.value)} checked={answer === "{props.id} + 1"}/>  
                <label htmlFor="answer2">{props.answer2}</label>
            </div>
            <div className="p-field-radiobutton">
                <RadioButton inputId={props.id} name={props.name} value="{props.id} + 2" onChange={(e) => setAnswer(e.value)} checked={answer === "{props.id} + 2"}/>  
                <label htmlFor="answer3">{props.answer3}</label>
            </div>
            <div className="p-field-radiobutton">
                <RadioButton inputId={props.id} name={props.name} value="{props.id} + 3" onChange={(e) => setAnswer(e.value)} checked={answer === "{props.id} + 3"}/>  
                <label htmlFor="answer4">{props.answer4}</label>
            </div>
            <Button label="Submit" onClick={chk}></Button>
        </div>
    )
}

export default Answers
